import { cloneElement } from 'react';
import { defaultRealTimePreset } from '@splunk/dashboard-utils/time';

const noop = () => {};

const SplunkwebConnector = ({ children, ...rest }) =>
    cloneElement(children, {
        ...rest,
        presets: defaultRealTimePreset,
        parseEarliest: null,
        parseLatest: null,
        onRequestParseEarliest: noop,
        onRequestParseLatest: noop,
    });

export default SplunkwebConnector;
